export type BufferEncoding = "ascii" | "utf8" | "utf-8" | "utf16le" | "ucs2" | "ucs-2" | "base64" | "latin1" | "binary" | "hex";
export interface FileSystemWrapper {
    canUseSymLink: boolean;
    writeFile(path: string, content: any, options?: {
        encoding?: BufferEncoding;
        mode?: string;
        flag?: string | number;
    }): Promise<void>;
    readFile(path: string, options?: {
        encoding?: BufferEncoding;
        flag?: string | number;
    }): Promise<string>;
    mkdir(path: string, options?: {
        recursive: boolean;
    }): Promise<void>;
    rmdir(path: string, options?: {
        recursive: boolean;
    }): Promise<void>;
    unlink(path: string): Promise<void>;
    stat(path: string): Promise<{
        isDirectory: boolean;
        isFile: boolean;
        isSymbolicLink: boolean;
    } | undefined>;
    symlink(sourcePath: string, destanationPath: string): Promise<void>;
    dirlength(path: string): Promise<number>;
    rmLogged(path: string, options?: {
        recursive: boolean;
    }): Promise<void>;
    mkdirLogged(path: string, options?: {
        recursive: boolean;
    }): Promise<void>;
}
